import { getPath, commonContainer, convertDateToTicks } from '@tezjs/common';
import mri from 'mri';
import * as tez from '@tezjs/vite';

async function buildCommand() {
  tez.build();
}

async function devCommand() {
  tez.tzServer();
}

const COMMANDS = {
  "dev": devCommand,
  "build": buildCommand
};

async function runCommand() {
  const args = mri(process.argv.slice(2), {
    boolean: [
      "no-clear"
    ]
  });
  const baseArguments = args._;
  const commandName = baseArguments.shift();
  let rootPath = baseArguments.length > 0 ? getPath([process.cwd(), baseArguments.pop()], false) : process.cwd();
  commonContainer.buildOptions = { buildVersion: convertDateToTicks(new Date()), mode: args.mode === "dev" ? "" : args.mode, rootDir: rootPath, port: args.port || 3e3, commandName };
  if (COMMANDS[commandName]) {
    COMMANDS[commandName]();
  }
}

function main() {
  runCommand().then((t) => {
  });
}

export { main };
